    private void drawPixels(RedBlueGrid grid, int[][] pixels, int startX, int startY, Color color) {
        for (int i = 0; i < pixels.length; i++) {
            for (int j = 0; j < pixels[i].length; j++) {
                if (pixels[i][j] == 1) {
                    grid.setColor(startY + i, startX + j, Color.BLUE);
                }
                if (pixels[i][j] == 2) {
                    grid.setColor(startY + i, startX + j, Color.RED);
                }
            }
        }
    }






